(function( $ ) {$.fn.matchH = function( target ) { var thisMaxHeight = 0; this.each(function() {thisMaxHeight = Math.max( thisMaxHeight, $(this).outerHeight() ); }); if ( !target ) {this.each( function() { $(this).height(thisMaxHeight); }); } else { var tHeight = $(target).outerHeight();if ( tHeight <= thisMaxHeight) { this.each( function() { $(this).height(thisMaxHeight); }); } else { this.each(function() { $(this).height(tHeight); }); } }};})( jQuery );